@namespace    USO Archive
@version      20200327.10.01
@description  HackerNews using colors from One Dark (https://atom.io/themes/one-dark-ui)
@author       Zac Webb
@license      CC0-1.0==/UserStyle== */
@-moz-document domain("news.ycombinator.com") {
body {
    background: #282c34;
}

body > center > table {
